import { useState } from 'react'
import Filter from './components/Filter'

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456', id: 1 },
    { name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
    { name: 'Dan Abramov', number: '12-43-234345', id: 3 },
    { name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 }
  ])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [newSearch, setNewSearch] = useState('')

  const handleSubmit = (e) => {
    e.preventDefault()
    const alreadyOnPersons = persons.reduce(
      (test,person) => {
        return test = (person.name === newName)
      },false)


    if (alreadyOnPersons === true) {
      alert(`${newName} is already added to phonebook`)
    } else {
      setPersons(persons.concat({name: newName, number: newNumber}))
    }
    setNewName('')
  }

const displayedPersons = newSearch 
  ? persons.filter(person => person.name.toUpperCase().includes(newSearch.toUpperCase()))
  : persons

  return (
    <div>
      <h2>Phonebook</h2>
      <div>
        filter shown with <input 
          id="filter-input"
          value={newSearch}
          onChange={(e) => setNewSearch(e.target.value)}
        />
      </div>
      <h2>add a new</h2>
      <form onSubmit={handleSubmit}>
        <div>
          name: <input 
            id="name-input"
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
          />
        </div>
        <div>
          number: <input 
            id="number-input"
            value={newNumber}
            onChange={(e) => setNewNumber(e.target.value)}
          />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <div>
        {displayedPersons.map(person =>
          <div key={person.name}>{person.name} {person.number}</div>)}
      </div>
      <div>debug: {newName} {newNumber}</div>
    </div>
  )
}

export default App
